chrome.runtime.onMessage.addListener((e,o,t)=>{if(e.action==="saveCourses"&&chrome.storage.local.set({courses:e.courses,academicTerm:e.academicTerm}),e.action==="getCourses")return chrome.storage.local.get(["courses","academicTerm"],r=>{t({courses:r.courses||[],academicTerm:r.academicTerm||null})}),!0;if(e.action==="saveFilterOption"&&chrome.storage.local.set({filterOption:e.filterOption}),e.action==="getFilterOption")return chrome.storage.local.get("filterOption",r=>{t(r.filterOption||"registered")}),!0});
